insert into books(name,price,sale_price,intro,content,image,quantity,author_id,category_id)
values 
(N'Quẳng gánh lo đi và vui sống', 125000, 75000, 'Bất kỳ ai đang sống đều sẽ có những lo lắng thường trực về học hành, công việc, những hoá đơn, chuyện nhà cửa,… Cuộc sống không dễ dàng giải thoát bạn khỏi căng thẳng, ngược lại, nếu quá lo lắng, bạn có thể mắc bệnh trầm cảm. Quẳng Gánh Lo Đi Và Vui Sống khuyên bạn hãy khóa chặt dĩ vãng và tương lai lại để sống trong cái phòng kín mít của ngày hôm nay. Mọi vấn đề đều có thể được giải quyết, chỉ cần bạn bình tĩnh và xác định đúng hành động cần làm vào đúng thời điểm.', 'Quẳng Gánh Lo Đi Và Vui Sống khuyên bạn những cách để giảm thiểu lo lắng rất đơn giản như chia sẻ nó với người khác, tìm cách giải quyết vấn đề, quên tất cả những điều lo lắng nằm ngoài tầm tay, Cố gắng thực tập điều này hàng ngày và trong cuộc sống chắc hẳn bạn sẽ thành công, có thể, không được như bạn muốn, nhưng chỉ cần bớt đi một chút phiền muộn thì cuộc sống của bạn đã có thêm một niềm vui. Nhờ các phương pháp của Dale Carnegie, hàng triệu người đọc đã xây dựng được thái độ sống tích cực, an tâm cảm nhận hạnh phúc và mãi mãi loại bỏ thói quen lo lắng.', 'https://salt.tikicdn.com/cache/750x750/ts/product/a6/bc/cc/d5eedb79cb55fb36c54ea85678a689b5.png.webp', 33, 13,3),
(N'Trên đường băng', 100000, 74000, 'TRÊN ĐƯỜNG BĂNG là cuốn sách tập hợp những bài viết truyền cảm hứng và hướng dẫn kỹ năng cho các bạn trẻ khi chuẩn bị bước vào đời.', 'Trong mỗi phần sẽ được chia làm nhiều chương nhỏ và mỗi chương là một câu chuyện ngắn khác nhau từ những trải nghiệm tại một đất nước nào đó, cảm nhận về những con người đó, những câu chuyện về tầm quan trọng của ngoại ngữ và chuyện học, từ chuyện đi làm đến các đối mặt với tiền,... để sau từng câu chuyện, người đọc sẽ có những nhận thức tích cực và đúng đắn hơn.', 'https://salt.tikicdn.com/cache/750x750/ts/product/f1/c8/c3/949032244f5f218db3e7dbc430802b82.jpg.webp', 14,3,3),
(N'Hạt giống tâm hồn 5: Và ý nghĩa cuộc sống', 90000, 65000, 'Khi đến với cuộc sống này, bất kỳ ai trong chúng ta cũng mong muốn lớn lên có được một tương lai tươi đẹp. Chúng ta ấp ủ trong lòng nhiều hoài bão, ước mơ chinh phục, khám phá bao điều mới lạ trên thế giới. Chúng ta tin tưởng bản thân có thể vươn lên tự khẳng định mình. Thế nhưng thực tế là hành trình cuộc sống không phải lúc nào cũng diễn ra trên con đường bằng phẳng và sáng sủa.', 'Sẽ có những lúc bạn không thể tránh khỏi những thử thách, cạm bẫy khiến bạn phạm sai lầm, đối diện với nỗi đau và cả sự mất mát. Sau khi đạt được tấm bằng cử nhân và bước vào thế giới công sở, bạn phát hiện công việc mình đang làm không giống như những gì mình tưởng tượng khi theo học ngành nghề này và trở nên hoang mang, mất phương hướng. Bạn chán nản, mất lòng tin vào tình yêu vì mối quan hệ nồng nhiệt thuở ban đầu nay đã phai nhạt. Bạn tuyệt vọng vì bị căn bệnh ác tính cướp đi sức khỏe và thậm chí ngăn cản bạn thực hiện ước mơ của mình. Khi gặp nghịch cảnh hay những điều không như ý, bạn có thể tự hỏi: Ý nghĩa của cuộc sống này thật sự là gì?', 'https://salt.tikicdn.com/cache/750x750/ts/product/25/2f/ac/d76faf5c6eff35f9c7799863fc8417a2.jpg.webp', 15,3,3),
(N'Tôi Thấy Hoa Vàng Trên Cỏ Xanh',130000,120000,'Ta bắt gặp trong Tôi Thấy Hoa Vàng Trên Cỏ Xanh một thế giới đấy bất ngờ và thi vị non trẻ với những suy ngẫm giản dị thôi nhưng gần gũi đến lạ. Câu chuyện của Tôi Thấy Hoa Vàng Trên Cỏ Xanh có chút này chút kia, để ai soi vào cũng thấy mình trong đó, kiểu như lá thư tình đầu đời của cu Thiều chẳng hạ ngây ngô và khờ khạo.','Cuốn sách viết về tuổi thơ nghèo khó ở một làng quê, bên cạnh đề tài tình yêu quen thuộc, lần đầu tiên Nguyễn Nhật Ánh đưa vào tác phẩm của mình những nhân vật phản diện và đặt ra vấn đề đạo đức như sự vô tâm, cái ác. 81 chương ngắn là 81 câu chuyện nhỏ của những đứa trẻ xảy ra ở một ngôi làng: chuyện về con cóc Cậu trời, chuyện ma, chuyện công chúa và hoàng tử, bên cạnh chuyện đói ăn, cháy nhà, lụt lội, Tôi thấy hoa vàng trên cỏ xanh hứa hẹn đem đến những điều thú vị với cả bạn đọc nhỏ tuổi và người lớn bằng giọng văn trong sáng, hồn nhiên, giản dị của trẻ con cùng nhiều tình tiết thú vị, bất ngờ và cảm động trong suốt hơn 300 trang sách. Cuốn sách, vì thế có sức ám ảnh, thu hút, hấp dẫn không thể bỏ qua.','https://salt.tikicdn.com/cache/750x750/ts/product/d5/56/6b/506b4f5baeed19a459dce7cea095787e.jpg.webp',40,1,1),
(N'Những Người Hàng Xóm',130000,120000,'Như một cuốn phim đầy màu sắc với âm điệu dịu dàng, êm ả. Cuộc sống bình yên của những người yêu thương nhau. Bài học về tình người đứng phía sau bài học về nghề viết, và cả trong câu chuyện về… một lối kinh doanh nhà cực kỳ đặc biệt! ','Câu chuyện đi theo lời kể của một anh chàng mới lấy vợ, chuẩn bị đi làm và có ý thích viết văn. Anh chàng yêu vợ theo cách của mình, khen ngợi sùng bái người yêu cũng theo cách của mình, nhưng nhìn cuộc đời theo cách sống của những người hàng xóm. Sống trong tình yêu của vợ đầy mùi thơm và nhiều vị ngọt. Chứng kiến tình yêu của anh cảnh sát với cô bạn gái ngành y; mối tình thứ hai của người phụ nữ tốt bụng phát thanh viên ngôn ngữ ký hiệu. Và được chiêm nghiệm trong tình yêu đắm đuối mỗi ngày của ông họa sĩ già thương nhớ người vợ xinh đẹp-người mẫu, nàng thơ của ông.','https://salt.tikicdn.com/cache/750x750/ts/product/a2/f0/e7/d7bc5cb5e12fac78066eb630a077ae95.jpg.webp',40,1,1),
(N'Còn chút gì để nhớ',130000,120000,'','Đó là những kỷ niệm thời đi học của Chương, lúc mới bước chân vào Sài Gòn và làm quen với cuộc sống đô thị. Là những mối quan hệ bạn bè tưởng chừng hời hợt thoảng qua nhưng gắn bó suốt cuộc đời. Cuộc sống đầy biến động đã xô dạt mỗi người mỗi nơi, nhưng trải qua hàng mấy chục năm, những kỷ niệm ấy vẫn luôn níu kéo Chương về với một thời để nhớ.','https://salt.tikicdn.com/cache/750x750/ts/product/5d/33/2f/c9f34abe1b95dd67a364bf72c54bad29.jpg.webp',40,1,1),
(N'Bàn Có Năm Chỗ Ngồi',130000,110000,'','Tác giả quen thuộc của thiếu nhi, của tuổi ô mai. Những tập truyện của anh luôn dí dỏm, hài hước, đem lại nhiều cảm xúc, nhiều bài học nhẹ nhàng cho lứa tuổi học trò. Hãy đọc để cùng gặp những nhân vật rất dễ thương, hoặc phải vượt qua những hoàn cảnh khó khăn, hoặc sẵn lòng chia sẻ với bạn bè, người thân… Thậm chí cả những nhân vật tạm gọi là phe xấu với nhiều trò đùa nghịch… cũng mang dáng dấp hồn nhiên. Tất cả hướng về CHÂN – THIỆN – MỸ. Họ là năm người bạn với năm cá tính và hoàn cảnh khác nhau cùng chung trong một lớp học. Những trò nghịch ngợm trẻ con đôi khi gây ra mâu thuẫn, nhưng trên tất cả đó là những đứa trẻ ham học, giàu lòng nhân ái và biết quan tâm đến bạn bè. Cảm thông với hoàn cảnh của nhau, từng bạn nghĩ ra cách giúp đỡ bạn mình theo khả năng để tình bạn ấy lớn dần theo năm tháng.','https://salt.tikicdn.com/cache/750x750/ts/product/68/bc/67/3ed5b5da03a5b8745a5678b225f13be4.jpg.webp',40,1,1),
(N'Con Chó Nhỏ Mang Giỏ Hoa Hồng',130000,100000,'','Câu chuyện về 5 chú chó đầy thú vị và cũng không kém cảm xúc lãng mạn- tác phẩm mới nhất của nhà văn bestseller Nguyễn Nhật Ánh sẽ khiến bạn thay đổi nhiều trong cách nhìn về loài thú cưng số 1 thế giới này.','https://salt.tikicdn.com/cache/750x750/ts/product/2d/ff/93/cf76ac4703f587c3188c5438dfb55af3.jpg.webp',40,1,1),
(N'Làm Bạn Với Bầu Trời',130000,110000,'Một câu chuyện giản dị, chứa đầy bất ngờ cho tới trang cuối cùng. Vẻ đẹp lộng lẫy, vì lòng vị tha và tình yêu thương, khiến mắt rưng rưng vì một nỗi mừng vui hân hoan. Cuốn sách như một đốm lửa thắp lên lòng khát khao sống tốt trên đời.','Viết về điều tốt đã không dễ, viết sao cho người đọc có thể đón nhận đầy cảm xúc tích cực, và muốn được hưởng, được làm những điều tốt dù nhỏ bé mới thật là khó. Làm bạn với bầu trời của Nguyễn Nhật Ánh đã làm được điều này. Như nhà văn từng phát biểu điểm mạnh của văn chương nằm ở khả năng thẩm thấu. Bằng hình thức đặc thù của mình, văn chương góp phần mài sắc các ý niệm đạo đức nơi người đọc một cách vô hình. Bồi đắp tâm hồn và nhân cách một cách âm thầm và bền bỉ, đó là chức năng gốc rễ của văn chương, đặc biệt là văn chương viết cho thanh thiếu niên.','https://salt.tikicdn.com/cache/750x750/ts/product/af/a1/4b/92477ec9b6688060b2b5d2022a60d3e6.jpg.webp',40,1,1),
(N'Sách Nấu Ăn Gia Đình ',100000,55000,'Mỗi dân tộc ở mỗi vùng lãnh thổ khác nhau trên thê giới đều có tập quán ăn uống và món ăn riêng. Song, dân tộc nào cũng có các loại thức ăn thường ngày trong gia đình, các bữa ăn tươi, các bữa cỗ, bữa tiệc, Dù là bữa ăn được tổ chức dưới bất cứ hình thức nào, mọi người cũng đều ưa thích được, thưởng thức một bữa ăn ngon. Nhưng, một bữa ăn ngon thật sự là gì? Chúng phải có sự phong phú và ngon miệng, tạo được sự thích thú, và trên hết','','https://salt.tikicdn.com/cache/750x750/ts/product/12/b3/49/21aecd05f04735d77f41e9504e5187b8.jpg.webp',40,2,2),
(N'Cây cam ngọt của tôi', 100000, 69000, 'Vị chua chát của cái nghèo hòa trộn với vị ngọt ngào khi khám phá ra những điều khiến cuộc đời này đáng số một tác phẩm kinh điển của Brazil.', 'Hãy làm quen với Zezé, cậu bé tinh nghịch siêu hạng đồng thời cũng đáng yêu bậc nhất, với ước mơ lớn lên trở thành nhà thơ cổ thắt nơ bướm. Chẳng phải ai cũng công nhận khoản đáng yêu kia đâu nhé. Bởi vì, ở cái xóm ngoại ô nghèo ấy, nỗi khắc khổ bủa vây đã che mờ mắt người ta trước trái tim thiện lương cùng trí tưởng tượng tuyệt vời của cậu bé con năm tuổi.
Có hề gì đâu bao nhiêu là hắt hủi, đánh mắng, vì Zezé đã có một người bạn đặc biệt để trút nỗi lòng: cây cam ngọt nơi vườn sau. Và cả một người bạn nữa, bằng xương bằng thịt, một ngày kia xuất hiện, cho cậu bé nhạy cảm khôn sớm biết thế nào là trìu mến, thế nào là nỗi đau, và mãi mãi thay đổi cuộc đời cậu.
Mở đầu bằng những thanh âm trong sáng và kết thúc lắng lại trong những nốt trầm hoài niệm, Cây cam ngọt của tôi khiến ta nhận ra vẻ đẹp thực sự của cuộc sống đến từ những điều giản dị như bông hoa trắng của cái cây sau nhà, và rằng cuộc đời thật khốn khổ nếu thiếu đi lòng yêu thương và niềm trắc ẩn. Cuốn sách kinh điển này bởi thế không ngừng khiến trái tim người đọc khắp thế giới thổn thức, kể từ khi ra mắt lần đầu năm 1968 tại Brazil.', 'https://salt.tikicdn.com/cache/750x750/ts/product/5e/18/24/2a6154ba08df6ce6161c13f4303fa19e.jpg.webp', 50, 3, 1),
('Hiểu về trái tim', 150000, 128000, 'Là tác phẩm đầu tay của nhà sư Minh Niệm, người sáng lập dòng thiền hiểu biết (Understanding Meditation), kết hợp giữa tư tưởng Phật giáo Đại thừa và Thiền nguyên thủy Vipassana, nhưng Hiểu Về Trái Tim không phải tác phẩm thuyết giáo về Phật pháp. Cuốn sách rất đời với những ưu tư của một người tu nhìn về cõi thế. Ở đó, có hạnh phúc, có đau khổ, có tình yêu, có cô đơn, có tuyệt vọng, có lười biếng, có yếu đuối, có buông xả… Nhưng, tất cả những hỉ nộ ái ố ấy đều được khoác lên tấm áo mới, một tấm áo tinh khiết và xuyên suốt, khiến người đọc khi nhìn vào, đều thấy mọi sự như nhẹ nhàng hơn…', 'Gần 500 trang sách, Hiểu Về Trái Tim là những phác thảo rõ nét về bức tranh đời sống cảm xúc của tất cả mọi người. Người đọc sẽ tìm thấy căn nguyên thành hình của những xúc cảm, thấy cả việc chúng chi phối thế nào đến hành xử thường ngày và quan trọng hơn cả là cách thức để điều khiển chúng thế nào. Không có câu trả lời cuối cùng của việc đúng – sai trong từng tình huống nhưng Hiểu Về Trái Tim có chứa trong nó chìa khóa để mở ra một cánh cửa đến với thế giới mới, thế giới an lạc từ trong tâm mỗi người. Bởi, suy cho cùng, mỗi trái tim - cơ quan chúng ta thường gắn cho nhiệm vụ điều khiển trí tuệ cảm xúc của con người, đều có những nỗi niềm riêng. Chỉ cần hiểu câu chuyện của trái tim, tự khắc, mỗi người sẽ quyết định được câu chuyện của chính mình. Bí quyết của sự chuyển hóa là không nên dùng ý chí để áp đặt hay nhồi nặn tâm mình trở thành một kiểu mẫu tốt đẹp nào đó. Chỉ cẩn quan sát và thấu hiểu chúng là đủ. Tác giả nhận định: Việc đưa tâm thức vượt lên những cung bậc cao hơn để nhìn đúng đắn hơn về thân phận của mình và bản chất cuộc sống là điều hoàn toàn có thể làm được', 'https://salt.tikicdn.com/cache/750x750/media/catalog/producttmp/1b/d0/54/3f23c30055381c7e58af80a62ce28fa5.jpg.webp', 20, 4, 3),
('Lý thuyết trò chơi', 160000, 109000, 'Đời người giống như trò chơi, mỗi bước đều phải cân nhắc xem đi như thế nào, đi về đâu, phải kết hợp nhiều yếu tố lại chúng ta mới có thể đưa ra được lựa chọn. Mà trong quá trình chọn lựa này các yếu tố khiến ta phải cân nhắc và những đường đi khác nhau sẽ ảnh hưởng trực tiếp đến kết quả.', 'Cuốn sách Lý thuyết trò chơi là bách khoa toàn thư về tâm lý học, về tẩy não và chống lại tẩy não, thao túng và chống lại thao túng, thống trị và chống lại thống trị. Cuốn sách giới thiệu công thức chiến thắng cho những trò chơi đấu trí giữa người với người trong cuộc sống hằng ngày; phân tách các khái niệm lý thuyết trò chơi vốn mơ hồ trở thành ngôn ngữ dễ hiểu và kết nối liền mạch với nghệ thuật tâm lý học; cho phép bạn nắm vững những bí ẩn của trò chơi tâm lý trong thời gian ngắn nhất.
Những kỹ năng trong Lý thuyết trò chơi có thể giúp chúng ta đọc thấu hoạt động tâm lý người khác, và từ đó chiếm thế chủ động trong trò chơi đấu trí giữa những người xung quanh.', 'https://salt.tikicdn.com/cache/750x750/ts/product/8a/b6/ba/1d95b88597f28e42d8ca91e3b3ff600f.jpg.webp', 20, 5, 3),
('Bạn không thông minh lắm đâu', 100000, 94000, 'Cho dù bạn có đang chọn mua smartphone hay đang quyết định xem nên tin theo chính trị gia nào, thì bạn cũng luôn tự cho mình là một cá thể đầy lý trí. Tất cả mọi suy nghĩ, hành động của bạn đều dựa trên những luận điểm, luận cứ rõ ràng, logic và trung lập. Nhưng đây mới là thực tế này: Bạn – Không – Thông – Minh - Lắm - Đâu. Bạn cũng chỉ là một kẻ hay tự dối mình như tất cả những người khác - nhưng đừng lo, bởi ảo tưởng là một phần của bản chất của con người.', 'Phát triển từ trang blog nổi tiếng của tác giả David McRaney, Bạn Không Thông Minh Lắm Đâu gợi mở cho bạn thấy nguồn gốc cho mọi quyết định, suy nghĩ, cảm xúc hóa ra lại đến từ nơi mà chẳng ai ngờ tới. Những bộ óc đã qua hàng triệu năm tiến hóa của con người đã thực hiện thành công một nhiệm vụ duy nhất: Bịa ra những câu chuyện, giả vờ là chúng ta đủ thông minh để hiểu hết mọi việc. Bạn sẽ được làm quen với những công cụ mà bộ não sử dụng, như Sự bất lực tự luyện, Sự bán rẻ, hay Ảo giác về sự minh bạch,… Cuốn sách giống như một tuyển tập những bài giảng Tâm lý học, với tất cả những phần nhám chán đã được lược đi.', 'https://salt.tikicdn.com/cache/750x750/ts/product/ab/05/d3/22a3d0e8d4ba523ce2d265a5c2f40c03.jpg.webp', 35, 6, 3),
('Bạn đỡ ngu ngơ rồi đấy', 150000, 103000, 'Sau thành công của cuốn sách đầu tay Bạn Không Thông Minh Lắm Đâu, tác giả David McRaney tiếp tục quay trở lại với sứ mệnh giúp chúng ta nhận ra mình đã bị bộ não lừa như thế nào, bằng đủ các thủ đoạn như tự bịa chuyện, ngụy biện dựa vào niềm tin phổ biến, ngụy biện về nhân quả hay hiệu ứng hào quang,…Ngoài ra thì bạn cũng sẽ biết thêm sự thật về cái gọi là bản ngã, giá của hạnh phúc, và làm thế nào để không rơi vào bẫy của bộ não từ bây giờ cho đến mãi mãi về sau.', 'Một sự pha trộn đầy tinh tế của Tâm lý học nghiêm túc và những chuyện tầm phào, Bạn Đỡ Ngu Ngơ Rồi Đấy dựa trên một sự thật rằng, tất cả chúng ta đều tin mình là những nhà quan sát đầy khách quan về thực tế, mọi thứ diễn ra y như chúng ta tai nghe mắt thấy- mà thực ra là không phải như vậy đâu. Nhưng không sao, những ảo tưởng đó đã tiến hóa qua hàng triệu năm chỉ nhằm mục đích giúp chúng ta tồn tại và tỉnh táo. Hãy hít một hơi thật sâu trước khi bước vào cuộc chiến với bản năng nào.', 'https://salt.tikicdn.com/cache/750x750/ts/product/ff/4b/8e/280c0534911ddb4b2aeda43a02e3be5c.jpg.webp', 35, 6,3), 
('Giận', 120000, 101000, 'Giận là một cuốn sách hay của Thiền sư Thích Nhất Hạnh, nó mở ra cho ta những khả năng kỳ diệu, nhưng lại rất dễ thực hành để ta tự mình từng bước thoát khỏi cơn giận và sống đẹp với xã hội quanh mình.', 'Khi ta giận, khi một ai đó làm cho ta giận, ta phải trở về với thân tâm và chăm sóc cơn giận của mình. Không nên nói gì hết. Không nên làm gì hết. Khi đang giận mà nói năng hay hành động thì chỉ gây thêm đổ vỡ mà thôi thế là không khôn ngoan. Phải trở về dập tắt lửa trước đã', 'https://salt.tikicdn.com/cache/750x750/ts/product/eb/aa/4f/5d08f95fb9956bbaca6aff31d7f28f6e.jpg.webp', 43, 7,4),
('Đường xưa mây trắng – Theo gót chân bụt', 250000, 214000, 'Đường Xưa Mây Trắng là một câu chuyện vô cùng lý thú về cuộc đời của Bụt được kể lại dưới ngòi bút hùng hồn đầy chất thơ của tác giả. Với văn phong nhẹ nhàng giản dị, với lối kể chuyện sinh động lôi cuốn, tác giả đã đưa chúng ta trở về tắm mình trong dòng sông Nguyên thỉ cách đây gần 2.600 năm, để được hiểu và gần gũi với một bậc giác ngộ mà cuộc đời của Ngài tỏa rạng nếp sống đầy tuệ giác và từ bi. Đọc Đường Xưa Mây Trắng cho chúng ta cảm tưởng như đang đọc một thiên tình sử, nhẹ nhàng, gần gũi mà sâu lắng.', 'Tôi đã viết những chương của Đường Xưa Mây Trắng với rất nhiều hạnh phúc. Thỉnh thoảng tôi đứng dậy pha trà để uống. Mỗi ngày viết mấy giờ cũng như được ngồi uống trà với Đức Thế Tôn. Và tôi biết trước người đọc sẽ rất có hạnh phúc vì khi viết, mình cũng đang có rất nhiều hạnh phúc. Viết Đường Xưa Mây Trắng không phải là một lao động mệt nhọc mà là cả một niềm vui lớn. Đó là một quá trình khám phá.
Có những đoạn tôi cho là khó viết, như đoạn Bụt độ ba anh em ông Ca Diếp. Tài liệu thường nói là Bụt độ ba anh em đó nhờ thần thông của Ngài nhưng khi viết thì tôi đã không để cho Bụt dùng thần thông mà cứ để Bụt sử dụng từ bi và trí tuệ của Ngài để độ ba ông ấy. Bụt có rất nhiều trí tuệ, rất nhiều từ bi, tại sao Bụt không dùng mà lại phải dùng thần thông? Và tôi có một niềm tin rất vững chãi là mình sẽ viết được chương đó. Chương này là một trong những chương khó nhất của Đường Xưa Mây Trắng nhưng cuối cùng tôi đã thành công.', 'https://salt.tikicdn.com/cache/750x750/ts/product/e6/86/f9/0ac8cf9fdd477515093c2bab5a7035a9.jpg.webp', 22, 7,4),
('Không diệt không sinh đừng sợ hãi', 100000, 80000, 'Nhiều người trong chúng ta tin rằng cuộc đời của ta bắt đầu từ lúc chào đời và kết thúc khi ta chết. Chúng ta tin rằng chúng ta tới từ cái Không, nên khi chết chúng ta cũng không còn lại gì hết. Và chúng ta lo lắng vì sẽ trở thành hư vô.', 'Bụt có cái hiểu rất khác về cuộc đời. Ngài hiểu rằng sống và chết chỉ là những ý niệm không có thực. Coi đó là sự thực, chính là nguyên do gây cho chúng ta khổ não. Bụt dạy không có sinh, không có diệt, không tới cũng không đi, không giống nhau cũng không khác nhau, không có cái ngã thường hằng cũng không có hư vô. Chúng ta thì coi là Có hết mọi thứ. Khi chúng ta hiểu rằng mình không bị hủy diệt thì chúng ta không còn lo sợ. Đó là sự giải thoát. Chúng ta có thể an hưởng và thưởng thức đời sống một cách mới mẻ.', 'https://salt.tikicdn.com/cache/750x750/ts/product/e0/15/3d/37a4ab0ab9169654f9b8ea56ca72f013.jpg.webp', 50, 7,3),
('Để có một tương lai', 120000, 106000, 'Trong xã hội có rất nhiều mối nguy. Nếu chúng ta đặt một người trẻ vào xã hội mà không tìm cách bảo vệ họ, họ sẽ tiếp nhận những bạo động, căm thù, sợ hãi và bất an mỗi ngày, rốt cuộc họ sẽ bị bệnh. Những câu chuyện của chúng ta, những chương trình tivi, quảng cáo, sách báo đều tưới tẩm các hạt giống khổ đau nơi những người trẻ và cả những người lớn. Chúng ta cần một vài nguyên tắc, vài phương thuốc cho căn bệnh của mình, những gì lành, đẹp và thật để có thể nương tựa vào.', '2.500 năm trước, Bụt đã đưa ra các nguyên tắc cho những người đệ tử tại gia của Ngài để giúp họ sống một đời sống bình an, lành mạnh và hạnh phúc. Đó là Năm Giới và nền tảng của mỗi Giới này là chánh niệm. Với chánh niệm, chúng ta ý thức được những gì đang xảy ra nơi thân thể, cảm thọ, tâm hồn và thế giới quanh ta, cũng như ta tránh không gây tổn hại cho mình và cho người.', 'https://salt.tikicdn.com/cache/750x750/ts/product/e1/d5/05/61c27b8622ec61cede93b88e617db0c5.jpg.webp', 57, 7,3),
('Tìm bình yên trong gia đình', 90000, 65000, 'Tập hợp nhiều câu hỏi vấn đáp của quý Phật tử, mọi người ở khắp nơi gửi về cho Sư ông để giải đáp những vấn đề xoay quanh các mối quan hệ trong gia đình, giữa vợ với chồng, cha mẹ với con cái và cả cách vượt qua nỗi đau khi mất đi người thân…', 'Cuộc đời là một chuỗi tương quan tương duyên. Không có một hiện hữu nào tự nó có được. Một bông hoa, không thể tự nó mà hình thành. Hoa phải nhờ những yếu tố khác như đất, nước, hạt giống, phân bón, người làm vườn… mới hình thành. Mình cũng vậy, cũng do nhiều yếu tố kết hợp, như cha mẹ, thức ăn, nước uống, môi trường, xã hội… Hạnh phúc và khổ đau cũng như thế, cũng do nhiều yếu tố tạo thành. Những yếu tố tương quan tương duyên đó dân gian gọi nôm na là duyên nợ, những gì đem lại hạnh phúc cho mình thì gọi là duyên, những gì đem lại khổ đau thì gọi là nợ. Đạo Bụt gọi đó là tương quan tương duyên hay tương tức. Cũng cùng một người nhưng khi thương thì gọi là mình có duyên với người đó. Rồi cũng người ấy, cũng tình thương ấy nhưng vì mình không biết trân quý và tưới tẩm để cho tình thương lớn lên để rồi khổ đau, thì lại gọi là mình có nợ với người ấy. Như thế thì vô lý và mâu thuẫn quá phải không?', 'https://salt.tikicdn.com/cache/750x750/ts/product/9a/8d/24/559a2e2c5ce2e40c37c800d94fea0b48.jpg.webp', 34, 7,3),
('Fear – Sợ hãi (Hóa giải sợ hãi bằng tình thương)', 90000, 75000, 'Hầu hết chúng ta ai cũng đã trải qua những giây phút hạnh phúc lẫn khó khăn trong cuộc sống. Tuy nhiên có nhiều người ngay lúc đang vui sướng nhất mà lòng vẫn trĩu nặng lo sợ, sợ ngày vui sẽ qua mau, sợ không như mong cầu, sợ phải xa cách người thương và một nỗi sợ lớn nhất, sợ thân xác mình sẽ tàn hoại. Cho nên ngay lúc biết bao điều kiện của hạnh phúc có đó niềm vui vẫn không trọn vẹn.', 'Tất cả chúng ta ai cũng từng lo sợ, nhưng nếu chúng ta biết nhìn sâu vào lo sợ thì ta có thể giải tỏa lo sợ và tìm lại nguồn vui. Lo sợ khiến chúng ta chú tâm về quá khứ hoặc lo lắng cho tương lai. Nếu chấp nhận lo sợ thì ta khám phá ra rằng ngay lúc này, hôm nay đây ta còn sống, cơ thể ta đang hoạt động diệu kỳ, mắt ta đang thấy được trời xanh, tai ta còn nghe được tiếng nói của người ta thương.', 'https://salt.tikicdn.com/cache/750x750/ts/product/63/30/9e/cacc0e688cb82558f93f1710ac36ca7c.jpg.webp', 56, 7,4),
('Giải phóng bộ não khỏi tư duy độc hại', 140000, 92000, 'Giải phóng bộ não khỏi tư duy độc hại tập trung vào việc khai thác nguồn gốc gây ra nỗi sợ để chấp nhận và khắc phục chúng, giúp chúng ta kiểm soát các suy nghĩ tiêu cực, cải thiện các mối quan hệ xã hội. Từ đó, giúp người đọc trả lời hai câu hỏi: Vì sao tâm trí bạn thường tự suy diễn ra những ý nghĩ tiêu cực? Làm thế nào để chống lại những suy nghĩ tự hủy hoại bản thân đang làm hại bạn mỗi ngày?', 'Có một nghịch lý rất quen thuộc là chúng ta thường xuyên được khuyên rằng hãy suy nghĩ kỹ càng trước khi hành động. Nhưng càng cố đào sâu suy nghĩ, càng cân nhắc cẩn thận, chúng ta càng dễ lún sâu vào những cái bẫy của tâm trí. Những cái bẫy đó là cảm xúc lo lắng, bồn chồn, là các luồng suy nghĩ lộn xộn, tiêu cực, mang hơi hướng tự hủy hoại bản thân. Chúng ta bế tắc và không thể thoát ra khỏi các lối mòn, để cuối cùng lại đưa ra những quyết định và hành động cực kỳ thiếu sáng suốt.', 'https://salt.tikicdn.com/cache/750x750/ts/product/cb/38/17/46685a8d8f209a63cf099c7a40fb68ac.jpg.webp', 65, 8,3),
('Không ai có thể làm bạn tổn thương trừ khi bạn cho phép', 90000, 70000, 'Chúng ta vẫn thường nghĩ mình sẽ chỉ hạnh phúc khi ở bên cạnh ai đó và nhận được yêu thương từ họ. Nhưng thực chất, hạnh phúc đơn giản chỉ là biết trân trọng bản thân và tận hưởng niềm vui trong chính cuộc sống mà bạn mong ước. Vậy nên, hãy nhớ rằng không ai có thể làm bạn tổn thương, trừ khi bạn cho phép.', 'Là người đang nghiên cứu và điều trị tâm lý, tác giả Yoo Eun Jung đã chỉ ra góc nhìn hoàn toàn khác về các vấn đề mà bạn gặp phải trong mối quan hệ với những người xung quanh. Quyển sách này vừa chứa đựng những lời tâm tình thủ thỉ của một người bạn, vừa đưa ra các liệu pháp cứng rắn ở cương vị một chuyên gia giúp bạn lần lượt gỡ bỏ những vướng mắc trong lòng. Nếu không phải là đơn thuốc hữu hiệu nhất cho những ai đang cảm thấy mệt mỏi với các mối quan hệ thì nó cũng sẽ là một viên vitamin, hay một viên thuốc bổ để bạn có thể mang theo và bổ sung như một loại dưỡng chất hàng ngày cho tâm lý. Mong bạn luôn giữ cho mình đủ sự thanh thản trong tâm hồn để biết ơn mỗi phút giây được sống, đủ quyết tâm để không đối xử tệ với bản thân cũng như đủ can đảm để bộc lộ những điều bản thân đang mưu cầu. Hãy bình tâm bước và nỗ lực không ngừng vì những điều bản thân mong muốn đạt được, bạn sẽ tự khắc thấy mình hạnh phúc hơn.', 'https://salt.tikicdn.com/cache/750x750/ts/product/0c/ff/1f/091c739d2cc4c1b2a3a9c5025930adcc.jpg.webp', 56, 9,3),
('Thao túng tâm lý trong tình yêu', 120000, 98000, 'Người bạn yêu đã bao giờ khiến bạn cảm thấy mình vô cùng yếu kém và không xứng đáng được hưởng hạnh phúc không? Bạn có thường xuyên cảm thấy mối quan hệ của mình độc hại, không lành mạnh không?', 'Bạn có tin vào những lời nói của đối phương, khiến bạn tự ti rằng bản thân chưa đủ tốt đẹp, giỏi giang không? Bạn nghĩ mình chỉ biết làm mọi thứ rối tung lên. Rất nhiều trục trặc xảy ra trong mối quan hệ là lỗi của bạn và bạn đáng phải chịu đựng những lời chỉ trích, chê bai của đối phương, đáng bị người kia đối xử lạnh nhạt. Tư duy này không hề đúng. Đừng để bị cuốn theo những lời lẽ thao túng của đối phương và để bản thân chìm trong biển suy nghĩ tiêu cực. Nếu bạn không cảm thấy hạnh phúc trong mối quan hệ tình cảm của mình, nhưng lại chưa đủ dũng khí và mạnh mẽ để thoát khỏi nó, cuốn sách này có thể giúp bạn.', 'https://salt.tikicdn.com/cache/750x750/ts/product/69/97/43/4f4d64eda540ba0a07bf21486d7740ed.jpg.webp', 54, 10,3),
('Muôn kiếp nhân sinh', 200000, 165000, 'Muôn kiếp nhân sinh là tác phẩm do Giáo sư John Vũ - Nguyên Phong viết từ năm 2017 và hoàn tất đầu năm 2020 ghi lại những câu chuyện, trải nghiệm tiền kiếp kỳ lạ từ nhiều kiếp sống của người bạn tâm giao lâu năm, ông Thomas – một nhà kinh doanh tài chính nổi tiếng ở New York. Những câu chuyện chưa từng tiết lộ này sẽ giúp mọi người trên thế giới chiêm nghiệm, khám phá các quy luật về luật Nhân quả và Luân hồi của vũ trụ giữa lúc trái đất đang gặp nhiều tai ương, biến động, khủng hoảng từng ngày.', 'Muôn kiếp nhân sinh cung cấp cho bạn đọc kiến thức mới mẻ, vô tận của nhân loại lần đầu được hé mở, cùng những phân tích uyên bác, tiên đoán bất ngờ về hiện tại và tương lai thế giới của những bậc hiền triết thông thái. Đời người tưởng chừng rất dài nhưng lại trôi qua rất nhanh, sinh vượng suy tử, mong manh như sóng nước. Luật nhân quả cực kỳ chính xác, chi tiết, phức tạp được thu thập qua nhiều đời, nhiều kiếp, liên hệ tương hỗ đan xen chặt chữ lẫn nhau, không ai có thể tính được tích đức này có thể trừ được nghiệp kia không, không ai có thể biết được khi nào nhân sẽ trổ quả. Nhưng, một khi đã gây ra nhân thì chắc chắn sẽ gặt quả - luật Nhân quả của vũ trụ trước giờ không bao giờ sai.', 'https://salt.tikicdn.com/cache/750x750/ts/product/30/ee/5a/e2aed009bb558b5d2cfbbe157b428ba4.jpg.webp', 32, 11,4),
('Trở về từ cõi sáng', 90000, 68000, 'Trở Về Từ Cõi Sáng viết về đời sống sau khi chết. Nếu khi còn sống chúng ta biết và hiểu về sự chết thì chết không có gì là đáng sợ nữa. Những chuyện có thật trong Trở Về Từ Cõi Sáng do nhà văn Nguyên Phong biên soạn và dịch thuật sẽ hé mở cánh cửa huyền bí tiết lộ vài điều bí mật về bên kia cửa tử.', 'Xuất bản lần đầu năm 1992, nó đã trở nên một best seller với số bản kỷ lục và dẫn đầu những cuốn sách bán chạy nhất Hoa Kỳ. Bắt đầu từ tháng 2 năm 1994, nó trở nên cuốn sách bán chạy nhất thế giới với mười tám ấn bản bằng các thứ tiếng khác nhau. Trong ấn bản đầu tại Âu châu, nhiều độc giả đã phải mua giá chợ đen vì nhà xuất bản in không kịp. Tại Nhật Bản, những người không muốn chờ đợi, đã xếp hàng để mua trước cửa nhà in, không đợi sách giao đến tiệm nữ Tại sao cuốn sách này lại bán chạy như vậy? Theo các nhà bình luận thì không những nội dung cuốn này phong phú hơn nhiều cuốn sách tương tự mà tác giả, bà Betty Eadie, đã kinh nghiệm được sự chết và kể lại những diễn tiến ở cõi bên kia, mà bà ta gọi là cõi sáng, một cách chi tiết rõ ràng. Sau khi cuốn sách xuất bản, tác giả đã được mời đi diễn thuyết khắp nơi, đâu đâu bà cũng thu hút được một cử tọa đông đảo chưa từng thấy. Nhiều đài phát thanh và truyền hình trên thế giới đã có chương trình phỏng vấn tác giả và thảo luận về hiện tượng người chết sống lại. Đây là một đề tài đang được bàn cãi rất sôi nổi, người tin kẻ ngờ, và có người đã chất vấn tác giả từng chi tiết một nhưng bà Eadie đã giải đáp được hầu hết thắc mắc của mọi người nên số người hâm mộ bà ngày càng nhiều.', 'https://salt.tikicdn.com/cache/750x750/media/catalog/producttmp/7d/06/d8/df16d1f8bbe59bf561650c6f9ae61338.jpg.webp', 23, 11,3),
('Hoa sen trên tuyết', 90000, 68000, 'Trên nền câu chuyện cuộc đời của nhân vật có thật, triệu phú Alan Havey, Hoa sen trên tuyết gieo vào độc giả nhiều điều phải nghĩ. Xuất thân từ một gia đình nghèo ở Mỹ và hiểu rõ giá trị của đồng tiền. Alan Havey đã nỗ lực rất nhiều để làm việc, học tập và đạt được những thành công nhất định: biệt thự lộng lẫy bên bờ Michigan, căn nhà nghỉ mát trên núi Mt Vernon, du thuyền, tài khoản kếch xù trong ngân hàng và một cô vợ đẹp như diễn viên điện ảnh… Cuộc sống đầy đủ cứ thế diễn ra cho tới khi những biến cố lần lượt đến trong cuộc đời: ông phát hiện bị ung thư; một số khoản đầu tư bị thua lỗ và sự nghiệp của ông có chiều hướng đi xuống. Và, người vợ - cũng là niềm tự hào của ông với mọi người đòi ly hôn để chia tài sản…', 'Tại sao ông lại phải gánh chịu những việc như vậy trong khi ông đã dành phần lớn thời gian, công sức của mình để làm việc và nỗ lực? Ông để vợ ông có cuộc sống tốt nhất nhưng cuối cùng ông vẫn bị bỏ rơi với hàng loạt cáo buộc? Thực sự, sống tốt, sống lành là tốt hay không? Câu trả lời rằm ở hành trình ông rời bỏ những bế tắc, theo lời khuyên của một người bạn, để thực hiện một chuyến du lịch không mục đích đến Dharamsala - nơi Đức Đạt Lai Lạt Ma và hàng chục nghìn người Tây Tạng đang tị nạn. Cả cuốn sách là những ghi chép tỉ mỉ về hành trình của ông, những người ông gặp để từ đó, mang đến lời giải đáp cho câu hỏi của chung rất nhiều người: Ý nghĩa của cuộc sống này là gì?', 'https://salt.tikicdn.com/cache/750x750/media/catalog/producttmp/b4/3f/8b/369c70a57ed11c8ac4f6e61fd6ed76ac.jpg.webp', 54, 11,3),
('Bên rặng tuyết sơn', 110000, 86000, 'Bên Rặng Tuyết Sơn là quyển sách mới trong bộ sách khoa học tâm linh nổi tiếng của dịch giả Nguyên Phong. Khơi nguồn từ vùng núi Himalaya xa xôi và vùng đồng bằng Ấn Độ, Bên Rặng Tuyết Sơn mang đến cho bạn đọc những sự thật vĩ đại về tâm linh và vai trò của việc làm chủ tâm linh cũng như làm chủ số phận. Tác phẩm còn khơi dậy những giá trị cao đẹp như: Tính trung thực, trái tim bao dung, lòng trắc ẩn, sự thông thái, lòng tín ngưỡng và tình yêu bao la.', 'Câu chuyện bắt đầu từ việc Satyakam đến thung lũng Saraswati để tầm sư học đạo. Nhưng không ngờ rằng, khi đến đây, thì vị đạo sư già đã chờ anh rồi, không những thế, ông còn nói đúng tên anh và biết anh đến gặp ông để làm gì dù rằng anh chưa hề xưng tên cũng như chưa nói mục đích của mình đến đây. Dài học đầu tiên của Satyakam là quên đi thời gian. Việc này nghe qua tưởng chừng như đơn giản nhưng khi bắt đầu thực hiện lại không đơn giản chút nào. Cũng như việc lắng nghe những âm thanh của vũ trụ như tiếng lá rơi, tiếng gió thổi, tiếng nước reo cũng không dễ dàng thực hiện nếu trong lòng ta vẫn còn nhiều tạp âm. Chúng ta sẽ được trải nghiệm những giây phút bình yên thông qua chuyến hành trình đi tìm chân lý của Satyakam dưới sự hướng dẫn của vị đạo sư trong dãy núi Tuyết Sơn để hiểu rõ hơn về sức mạnh vĩnh hằng của thế giới tâm linh cũng như khám phá chính tiếng nói nội tâm của bản thân mình.', 'https://salt.tikicdn.com/cache/750x750/ts/product/f5/97/18/8af8991fc4be253798723e4f595a5ef9.jpg.webp', 43, 11,4),
('Đường mây qua xứ tuyết', 140000, 88000, 'Đường Mây Qua Xứ Tuyết ghi lại những điều Anagarika Govinda chứng kiến trong thời gian du hành ở Tây Tạng. Hành trình của tác giả diễn ra vào khoảng thập niên 30 đến thập niên 50 của thế kỷ trước, trước thời kỳ diễn biến chính trị phức tạp dẫn đến sự sáp nhập vào lãnh thổ nước Cộng hòa Nhân dân Trung Hoa như hiện nay. Thời điểm đó, một phần phía tây của Tây Tạng bị xem như nằm dưới sự kiểm soát của chính quyền thuộc địa Anh nên việc tác giả đi từ Sri Lanka sang Ấn Độ rồi thâm nhập phía tây Tây Tạng (tất cả đều là thuộc địa của Anh), giấy tờ thông hành đều do người Anh kiểm soát. Về phần sau của hành trình, tác giả đi sâu vào phần phía đông Tây Tạng vốn thuộc sự quản lý của chính quyền Lạt Ma tại Lhasa nên lại phải xin cấp thêm giấy thông hành từ chính quyền này.', 'Trên thực tế, từ thời cổ đại cho đến tận lúc bấy giờ, Tây Tạng vốn chỉ được xem như một vùng đất bí ẩn khép kín, một mắt xích trên con đường tơ lụa huyền thoại nên dù đã nhiều lần bị xâm chiếm, các chính quyền đô hộ tạm thời đều dần dà bỏ rơi vùng đất này; do đó, nơi đây được đứng đầu bởi cố vấn tinh thần là các đức Lạt Ma mà cao nhất là Đạt Lai Lạt Ma. Từ góc nhìn của người phương Tây, Tây Tạng được xem như một phần của vương quốc Ấn Độ (vốn bao gồm cảcác nước Pakistan, Nepal, Bhutan, Bangladesh, ngày nay) do nơi đây chịu nhiều ảnh hưởng từ tín nguỡng và tôn giáoẤn Độ hơn từ phía Trung Hoa. ', 'https://salt.tikicdn.com/cache/750x750/ts/product/ae/50/91/aa6e99d756e146014de60be6ac33e443.jpg.webp', 76, 11,3),
('Một đời được mất', 150000,104000, 'Mọi vấn đề khó quyết định trong cuộc đời này, đều có thể suy xét dưới góc nhìn Được và Mất.', 'Có những thứ bạn nghĩ mình được, nhưng thực chất chỉ là ảo mộng hão huyền. Cũng có những thứ bạn nghĩ mình mất, nhưng cuộc sống chắc chắn sẽ trả lại cho bạn dưới một hình thức khác.', 'https://salt.tikicdn.com/cache/750x750/ts/product/1e/db/52/27e9eafb0522416321df0b1ba064c7d1.png.webp', 46, 12,3)
;
